import { LOGIN_REQUEST, LOGIN_SUCCES, LOGIN_ERROR } from "./actionType";
import Cookie from 'js-cookie'

const initialState = {
  users: [],
  loading: false,
  loaded: true,
  error: null,
  isAuth: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state,             //            1   loading:false     loaded:true             
        loading: true,          //          2   loaded               
        loaded:false,             //        3                 
      };
    case LOGIN_SUCCES:
      return {
        ...state,
        loading: false,
        loaded: false,
        isAuth: true,
        users: action.payload,
      };
    case LOGIN_ERROR:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default userReducer;
