import React, { Component } from "react";
import Home from "./Home";
import { I18nProvider, LOCALES } from "../i18nProvider";
import translate from "../i18nProvider/translate";
import { connect } from "react-redux";
import { Link, NavLink, withRouter, Redirect } from "react-router-dom";
import MyAccount from "./MyAccount";

// import cookie from "js-cookie";
import { fetchUsersLogin } from "../redux/Authentication/action";
import { setLocales } from "../redux/Language/action";
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
  }

  postLogin = (props) => {
    const data = {
      email: this.state.email,
      password: this.state.password,
    };
    this.props.fetchUsersLogin(data);
    if (true) {
      console.log("salutare");
    }
    // this.props.fetchUserData();
  };
  // if(true){

  // }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    console.log("succes");
    this.postLogin();
  };

  render() {
    if (!this.props.isNotLogged) {
      return (
        <I18nProvider locale={this.props.locales}>
          <div>
            <div>
              <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                English
              </button>{" "}
              |{" "}
              <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
                German
              </button>{" "}
              {""}|{" "}
              <button onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}>
                Romanian
              </button>{" "}
            </div>

            <form onSubmit={this.handleSubmit}>
              <h3>{translate("log-in")}</h3>
              <div>
                <label htmlFor="email">{translate("e-mail")}</label>
                <input
                  type="text"
                  name="email"
                  placeholder="Email"
                  onChange={this.handleChange}
                />
              </div>
              <div>
                <label htmlFor="password">{translate("pass-word")}</label>
                <input
                  type="password"
                  placeholder="Password"
                  name="password"
                  onChange={this.handleChange}
                />
              </div>
              <button type="submit">{translate("log-in")}</button>
            </form>
            <h3>
              Don't have an account yet ? <Link to="/register">sign-up</Link>
            </h3>
            <h3>
              Back to
              <Link to="/"> home </Link>
            </h3>
          </div>
        </I18nProvider>
      );
    } else {
      return (
        <div>
          <Redirect to="./MyAccount" />
        </div>
      );
    }
  }
}
const mapStateToProps = (state) => {
  return {
    userData: state.users,
    locales: state.locale.lang,
    isNotLogged: state.users.fetched,
   // auth: state.users.fetched,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUsersLogin: (data) => dispatch(fetchUsersLogin(data)),
    setLocale: (lang) => dispatch(setLocales(lang)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
