/** @format */

import React, { Component } from 'react';
import { connect } from 'react-redux';
import { findImages } from '../redux/image/action';

export class ImagePage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			imageList: this.props.image,
		};
	}

	componentDidMount = () => {
		this.props.findImage(this.props.client.id);
	};

	render() {
		if (this.props.loading == true) {
			return <div>Loading...</div>;
		} else {
			return (
				<div>
					<div>
						{this.state.imageList.map(({name,type,data,id},index)=>{
							<div>
								data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAASABIAAD/4SIpRXhpZgAAT
								<img src={`data:image/${type};base64,${data}`} />
								<div><p>{name}</p></div>
							</div>;
						})}
					</div>
				</div>
			);
		}
	}
}

const mapStateToProps = (state) => {
	return {
		locales: state.locale.lang,
		client: state.users.user,
		image: state.image.image,
		loading: state.image.loading,
	};
};
const mapDispatchToProps = (dispatch) => {
	return {
		findImage: (id) => dispatch(findImages(id)),
	};
};
export default connect(mapStateToProps, mapDispatchToProps)(ImagePage);
