import { LOGIN_REQUEST, LOGIN_SUCCES, LOGIN_ERROR } from "./actionType";

const initialState = {
  users: [],
  loading: false,
  fetched: fal,
  error: null,
  isAuth: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case LOGIN_SUCCES:
      return {
        ...state,
        loading: false,
        fetched: true,
        isAuth: true,
        users: action.payload,
      };
    case LOGIN_ERROR:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default userReducer;
