import { LOGIN_REQUEST, LOGIN_SUCCES, LOGIN_ERROR, EDIT_PROFILE } from "./actionType";
import Cookie from "js-cookie";

const initialState = {
  user:['empty'],
  loading: true,
  loaded: false,
  error: null,
  isAuth: false,
  edit:false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state, //            1   loading:true     loaded:false
        loading: true, //          2   loading:true      loaded:true
        loaded: true, //        3   loading:false     loaded:true
      };
    case LOGIN_SUCCES:
      return {
        ...state,
        loading: false,
        loaded: true,
        isAuth: true,
        user: action.payload,
       
      };
    case LOGIN_ERROR:
      return {
        ...state,
        loading: false,
        loaded: true,
        error: action.payload,
        isAuth: false,
      };
    case EDIT_PROFILE:
      return{
        ...state
      }  
    default:
      return state;
  }
};

export default userReducer;
