import {
  LOGIN_REQUEST,
  LOGIN_SUCCES,
  LOGIN_ERROR,
  EDIT_PROFILE,
} from "./actionType";

export const fetchUsersLogin = ({ email, password }) => {
  const data = {
    email,
    password,
  };
  return (dispatch) => {
    fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": `application/json` },
      body: JSON.stringify(data),
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        const user = { data: res.data, verifyToken: res.verifyToken };

        dispatch(loginSuccess(user));
      })
      .catch((err) => {
        dispatch(loginError(err));
      });
  };
};

export const fetchUsersRegister = (data) => {
  return (dispatch) => {

    fetch(`http://localhost:5000/register`, {
      method: "POST",
      body: JSON.stringify(data),
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        // res.verifyToken = "";
        const user = { data: res.data, verifyToken: res.verifyToken };
        if (user.verifyToken) {
          console.log("true");
        } else if (!user.verifyToken) {
          console.log("false");
        }
        dispatch(loginSuccess(user));
      })

      .catch((err) => {
        dispatch(loginError(err));
        console.error(err.value);
      });
  };
};

