import React, { Component } from "react";
import Register from "./Register";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { NavLink, withRouter, Redirect } from "react-router-dom";
import Cookie from "js-cookie";
import {
  verifyAuthentication,
  logoutUser,
} from "../redux/Authentication/action";
class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    console.log("I am in MyAccount");

    return (
      <h1>
          <I18nProvider locale={this.props.locales}>
          <div>
            <div>
              <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                English
              </button>{" "}
              |{" "}
              <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
                German
              </button>{" "}
              {""}|{" "}
              <button onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}>
                Romanian
              </button>{" "}
            </div>

            <form onSubmit={this.handleSubmit}>
              <h3>{translate("log-in")}</h3>
              <div>
                <label htmlFor="email">{translate("e-mail")}</label>
                <input
                  type="text"
                  name="email"
                  placeholder="Email"
                  onChange={this.handleChange}
                />
              </div>
              <div>
                <label htmlFor="password">{translate("pass-word")}</label>
                <input
                  type="password"
                  placeholder="Password"
                  name="password"
                  onChange={this.handleChange}
                />
              </div>
              <button type="submit">{translate("log-in")}</button>
            </form>
            <h3>
              Don't have an account yet ? <Link to="/register">sign-up</Link>
            </h3>
            <h3>
              Back to
              <Link to="/"> home </Link>
            </h3>
          </div>
        </I18nProvider>
        <NavLink to="/login" activeStyle={{ color: "red" }}>
          {" "}
          Login
        </NavLink>
        <NavLink to="/">You are in app so go back</NavLink>
        <button type="submit" onClick={this.props.logoutUser}>
          Logout
        </button>
      </h1>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isAuth: state.users.isAuth,
    user: state.users.users,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser()),

    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};
// export default MyAccount;

// class MyAccount extends Component {
//   state = {};
//   render() {
//     return <div onClick={this.handleaClink}></div>;
//   }
// }

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
