import React, { Component, useEffect } from "react";
import { Link, NavLink, withRouter, Redirect } from "react-router-dom";
import Notification from "./Notification";
import Home from "./Hosme";
import { connect } from "react-redux";
import { fetchUsersRegister } from "../redux/Authentication/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import translate from "../i18nProvider/translate";

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      firstName: "",
      lastName: "",
      phone: "",
    };
  }

  postRegister = () => {
    const data = {
      username: this.state.email,
      password: this.state.password,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      phone: this.state.phone,
    };
    this.props.fetchUsersRegister(data);
    if (this.props.registerSucces) this.props.goes();
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.postRegister();
    // if (this.postRegister()) {
    //   alert("user has been created succesfully");
    //  } else {
    //    alert("user could not be created");
    //  }
    //props.history.push("/");
    //return <Redirect to="/Notification" />;
  };
  render() {
    return (
      <I18nProvider locale={this.props.locales}>
        <div>
          <form onSubmit={this.handleSubmit}>
            <h3>{translate("register-")}</h3>

            <div>
              <label htmlFor="email">{translate("e-mail")}</label>
              <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="password">{translate("pass-word")}</label>
              <input
                type="password"
                placeholder="Password"
                name="password"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="firstName">
                {translate("first-name", "abc")}
              </label>
              <input
                type="text"
                name="firstName"
                placeholder="First Name"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="lastName">{translate("last-name")}</label>
              <input
                type="text"
                name="lastName"
                placeholder="Last Name"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="phone">{translate("phone-number")}</label>
              <input
                type="text"
                name="phone"
                placeholder="Phone"
                onChange={this.handleChange}
              />
            </div>
            <button type="submit">{translate("register-")}</button>
          </form>
        </div>
      </I18nProvider>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    registerSucces: state.users.feched,
    locales: state.locale.lang,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUsersRegister: (data) => dispatch(fetchUsersRegister(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Register);
