import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import Cookies from 'js-cookie'

import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Link,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";
import {
  fetchUsersData,
  fetchUsersLogin,
} from "../redux/Authentication/action";

class Home extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     isLoggedIn: true,
  //   };
  // }
  // change = () => {
  //   this.setState({
  //     isLoggedIn: false,
  //   });
  // };
  // handleClick = () => {};
  handleMyAccount = () => {
    this.props.fetchUsersData();
  };
  render() {
    if (!this.props.isNotLogged) {
      return (
        <I18nProvider locale={this.props.locales}>
          <div>
            <div>
              <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                English
              </button>{" "}
              |{" "}
              <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
                German
              </button>{" "}
              {""}|{" "}
              <button onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}>
                Romanian
              </button>{" "}
            </div>
            <h1>
              Welcome to the Homepage and isAuth is:

              {console.log(this.props.isAuth)}
            </h1>
            {/* {    <button onClick="">GETGETGET</button> */}
            {/*  <Login goes={this.change} gone={this.state.isLoggedIn} />
                     <Register goes={this.change} gone={this.state.isLoggedIn} />*/}
            <div>
              <NavLink to="/login" activeStyle={{ color: "red" }}>
                Login
              </NavLink>{" "}
              <NavLink to="/register" activeStyle={{ color: "red" }}>
                Register
              </NavLink>
            </div>
          </div>
        </I18nProvider>
      );
    } else if (this.props.isNotLogged) {
      return (
        <div>
          console.log({Cookies.get('name')})   
          <h1>
            Welcome in app    
            <Link onClick={this.handleMyAccount} to="./MyAccount">
              {" "}
              MyAccount{" "}
            </Link>
          </h1>
        </div>
      );
    }
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isNotLogged: state.users.fetched,
    isAuth: state.users.isAuth,
  };
};
// const mapStateToProps = (state) => {
//   return {
//     // locale: state.locale,
//   };
// };
// const mapDispatchToProps = (dispatch) => {
//   return {
//     //setLocale: (lang) => dispatch(setEnglish(lang)),
//   };
// };
const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUsersLogin: (data) => dispatch(fetchUsersLogin(data)),
    fetchUsersData: () => dispatch(fetchUsersData()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
