import React, { Component } from "react";
import Register from "./Register";
import { Link } from "react-router-dom";
import translate from "../i18nProvider/translate";
import { I18nProvider, LOCALES } from "../i18nProvider";
import { setLocales } from "../redux/Language/action";
import EditProfile from "./UserProfile"
import { connect } from "react-redux";
import { NavLink, withRouter, Redirect } from "react-router-dom";
import Cookie from "js-cookie";
import {
  verifyAuthentication,
  logoutUser, editProfile
} from "../redux/Authentication/action";
class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    console.log("I am in MyAccount");

    return (
      <div>
        <I18nProvider locale={this.props.locales}>
          <div>
            <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
              English
            </button>{" "}
            |{" "}
            <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
              German
            </button>{" "}
            {""}|{" "}
            <button onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}>
              Romanian
            </button>{" "}
          </div>
          <div>
            {(console.log(this.props.edit), console.log("1111111"))}
            EditProfile/>
          </div>
          <h1>
            <NavLink to="/login" activeStyle={{ color: "red" }}>
              {translate("log-in")}
            </NavLink>
            <NavLink to="/">You are in app so go back</NavLink>
            <button type="submit" onClick={this.props.editProfile}>
              Logout
            </button>   
          </h1>
        </I18nProvider>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isAuth: state.users.isAuth,
    client: state.users.user,
    edit: state.users.edit,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser()),
    setLocale: (lang) => dispatch(setLocales(lang)),
    editProfile:()=> dispatch(editProfile()),
    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};
// export default MyAccount;

// class MyAccount extends Component {
//   state = {};
//   render() {
//     return <div onClick={this.handleaClink}></div>;
//   }
// }

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
