import React from "react";
import { connect } from "react-redux";
import { Route, Redirect } from "react-router-dom";
import Home from "./Home";
import { verifyAuthentication } from "../redux/Authentication/action";

import Cookie from "js-cookie";

const PrivateRoute = ({
  verifyAuthentication,
  isAuth,
  loaded,
  client,
  loading,
  component: Component,
  ...rest
}) => {
  if (!loaded) {verifyAuthentication(client.data[0].verify);}
  console.log("I am in private dupa request");
  if (loading) {
    return <h1>Loading {console.log("sunt in loading")}</h1>;
  } else if(!loading){
    console.log("I am in private in SUCCES si intru in componenta");
    return (
      <Route
        {...rest}
        component={(props) =>
          isAuth ? <Component {...props} /> : <Redirect exact to="/" />
        }
      />
    );
  }
};

const mapStatetoProps = (state) => ({
  isAuth: state.users.isAuth,
  locales: state.locale.lang,
  loaded: state.users.loaded,
  client: state.users.user,
  loading: state.users.loading,
});
const mapDispatchToProps = (dispatch) => {
  return {
    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};

export default connect(mapStatetoProps, mapDispatchToProps)(PrivateRoute);
