import React, { Component } from "react";
import Home from "./Home";
import { I18nProvider, LOCALES } from "../i18nProvider";
import translate from "../i18nProvider/translate";
import { connect } from "react-redux";
// import cookie from "js-cookie";
import { fetchUsersLogin } from "../redux/Authentication/action";
import { setLocales } from "../redux/Language/action";
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
  }

  postLogin = (props) => {
    const data = {
      username: this.state.email,
      password: this.state.password,
    };
    this.props.fetchUsersLogin(data);
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    console.log("succes");
    this.postLogin();
  };

  render() {
    return (
      <I18nProvider locale={this.props.locales}>
        <div>
          {translate("e-mail")}
          <form onSubmit={this.handleSubmit}>
            <h3>{translate("log-in")}</h3>
            <div>
              <label htmlFor="email">{translate("e-mail")}</label>
              <input
                type="text"
                name="email"
                placeholder="Email"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="password">{translate("pass-word")}</label>
              <input
                type="password"
                placeholder="Password"
                name="password"
                onChange={this.handleChange}
              />
            </div>
            <button type="submit">{translate("log-in")}</button>
          </form>
        </div>
      </I18nProvider>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    userData: state.users,
    locales: state.locale.lang,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUsersLogin: (data) => dispatch(fetchUsersLogin(data)),
    setLocale: (lang) => dispatch(setLocales(lang)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
