/** @format */

export const findImages = (data) => {
	return (dispatch) => {
		fetch('/dia-aplication/api/user/image?id=' + data, {
			method: 'GET',
			headers: { 'Content-Type': 'application/json' },
			credentials: 'include',
		})
			.then((res) => res.json())
			.then((res) => {
				console.log('here images');

				dispatch(imageSuccess(res));
			})
			.catch((err) => {
				dispatch(imageFailure(err));
			});
	};
};
export const uplaodImage = (data) => {
	return (dispatch) => {
		fetch('/dia-aplication/api/user/image/upload', {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			credentials: 'include',
		})
			.then((res) => res.json())
			.then((res) => {
				console.log('here images');

				dispatch(imageSuccess(res));
			})
			.catch((err) => {
				dispatch(imageFailure(err));
			});
	};
};

export const imageSuccess = (res) => {
	return {
		type: "IMAGE_SUCCESS",
		payload: res,
	};
};


export const imageFailure = () => {
	return {
		type: 'IMAGE_FAILURE',
	
	};
};
