import React from "react";
import { connect } from "react-redux";
import { Route, Redirect } from "react-router-dom";
import Home from "./Home";
import {
  verifyAuthentication,
  verifyRefreshToken,
} from "../redux/Authentication/action";

const PrivateRoute = ({
  verifyAuthentication,
  verifyRefreshToken,
  isAuth,
  loaded,
  client,
  loading,
  component: Component,
  ...rest
}) => {
  console.log("sunt in private");
  if (!loaded) {
    if (!client.verifyToken || client.verifyToken == undefined) {
      console.log("sunt in refresh if");
      verifyRefreshToken();
    } else {
      console.log("sunt in verify if");

      verifyAuthentication(client.verifyToken);
      console.log("sunt in verify if");
    }
  }
  if(isAuth && !loading)
  console.log(`I am in private dupa request  ${client.verifyToken}`);
  if (loading) {
    return <h1>Loading {console.log("sunt in loading")}</h1>;
  } else if (!loading) {
    console.log(
      `I am in private in SUCCES si intru in componenta ${client.verifyToken}`
    );
  //  verifyAuthentication(client.verifyToken);
    return (
      <Route
        {...rest}
        component={(props) =>
          isAuth ? <Component {...props} /> : <Redirect exact to="/" />
        }
      />
    );
  }
};

const mapStatetoProps = (state) => ({
  isAuth: state.users.isAuth,
  locales: state.locale.lang,
  loaded: state.users.loaded,
  client: state.users.user,
  loading: state.users.loading,
});
const mapDispatchToProps = (dispatch) => {
  return {
    verifyRefreshToken: () => dispatch(verifyRefreshToken()),
    verifyAuthentication: (verifyToken) =>
      dispatch(verifyAuthentication(verifyToken)),
  };
};

export default connect(mapStatetoProps, mapDispatchToProps)(PrivateRoute);
