import {
  LOGIN_REQUEST,
  LOGIN_SUCCES,
  LOGIN_ERROR,
  EDIT_PROFILE,
} from "./actionType";

const initialState = {
  user: [],
  loading: true,
  loaded: false,
  verify: false,
  error: null,
  isAuth: false,
  edit: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state,
       
        loaded: true,
      };
    case LOGIN_SUCCES:
      return {
        ...state,
        loading: false,
          
        isAuth: true,
        user: action.payload,
      };
    case LOGIN_ERROR:
      return {                    // isAuth  false
        ...state,                 // loaded     true   || loaded       false
        loading: false,           //loading succes false || loaded succes true
        loaded: true,
        error: action.payload,
        isAuth: false,
      };
    case EDIT_PROFILE:
      return {
        ...state,
        edit: !state.edit,
      };
    default:
      return state;
  }
};

export default userReducer;
