import React from "react";
import { connect } from "react-redux";
import { Route, Redirect } from "react-router-dom";
import Home from "./Home";
import Cookie from "js-cookie";
import { verifyAuthentication, verifyRefreshToken } from "../redux/Authentication/action";

const PublicRoute = ({
  isAuth,
  loaded,
  loading,
  client,
  verifyAuthentication,
  verifyRefreshToken,
  component: Component,
  ...rest
}) => {
  if (!loaded) {
    if (!client.verifyToken || client.verifyToken == undefined) {
      console.log("sunt in refresh if");
      verifyRefreshToken();
    } else {
      console.log("sunt in verify if");

      verifyAuthentication(client.verifyToken);
      console.log("sunt in verify if");
    }
  }
