import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import Cookies from "js-cookie";
import Homepage from './Homepage'
import Dashboard from './Dashboard'
import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Link,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";
import {
  fetchUsersData,
  fetchUsersLogin,
  logoutUser,
  verifyAuthentication,
} from "../redux/Authentication/action";

class Home extends Component {
 
  render() {

    if (!this.props.loaded) {
      this.props.verifyAuthentication();
    }
    if (this.props.loading) {
      //
      return (
        <h1>
          Loading... 
        </h1>
      );
    } else if (!this.props.loading) {

      if (!this.props.isAuth) {
        return  <Homepage />;
      } else if (this.props.isAuth) {
        return  <Dashboard />
      }
    }
  } 
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    client: state.users.user,
    loaded: state.users.loaded,
    isAuth: state.users.isAuth,
    loading: state.users.loading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUsersData: () => dispatch(fetchUsersData()),
    verifyAuthentication: () => dispatch(verifyAuthentication()),
    logoutUser: () => dispatch(logoutUser()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
