import {
  LOGIN_REQUEST,
  LOGIN_SUCCES,
  LOGIN_ERROR,
  EDIT_PROFILE,
} from "./actionType";

export const fetchUsersLogin = ({ email, password }) => {
  const data = {
    email,
    password,
  };
  return (dispatch) => {
    dispatch(loginRequest());
    fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": `application/json` },
      body: JSON.stringify(data),
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        const user = { data: res.data, verifyToken: res.verifyToken };

        dispatch(loginSuccess({ user }));
      })
      .catch((err) => {
        dispatch(loginError(err));
      });
  };
};

export const fetchUsersRegister = (data) => {
  return (dispatch) => {
    dispatch(loginRequest());

    fetch(`http://localhost:5000/register`, {
      method: "POST",
      body: JSON.stringify(data),
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        // res.verifyToken = "";
        const user = { data: res.data, verifyToken: res.verifyToken };
        console.log(`here you have res.data ${user.verifyToken} ttt`);
        if (user.verifyToken) {
          console.log("true");
        } else if (!user.verifyToken) {
          console.log("false");
        }
        dispatch(loginSuccess(user));
      })

      .catch((err) => {
        dispatch(loginError(err));
        console.error(err.value);
      });
  };
};

export const fetchUsersData = (data) => {
  return (dispatch) => {
    dispatch(loginRequest());
    fetch("http://localhost:5000/MyAccount", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        dispatch(loginSuccess(data));
      })
      .catch((err) => {
        dispatch(loginError());
      });
  };
};
export const verifyAuthentication = (verifyToken) => {
  return (dispatch) => {
    dispatch(loginRequest());
    fetch(`http://localhost:5000/verifyToken`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        authorization: `${verifyToken}`,
      },
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        const user =  { data: res.data, verifyToken: res.verifyToken }

        dispatch(loginSuccess(user));
      })

      .catch((err) => {
        dispatch(loginError(err));
        console.error(err.value);
      });
  };
};

export const verifyRefreshToken =()=>{
  dispatch(loginRequest())
  fetch(`http://localhost:5000/refreshToken`,{
    mothod: "GET",
    headers: {"Content-Type": "application/json",},
    credentials: 'include'
  })
  .then((res)=>res.json())
  .then((res)=>{
    const user = res.data
    disptach(loginSuccess(user))
  })
}

export const fetchUsersRegister_GET = (data) => {
  return (dispatch) => {
    dispatch(loginRequest());

    fetch(`http://localhost:5000/register`, {
      method: "GET",
      body: JSON.stringify(),
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => {
        if (res.status === 200) {
          dispatch(loginSuccess(data));
        } else {
          console.log(res);
          const error = new Error(res);
          throw error;
        }
      })
      .catch((err) => {
        dispatch(loginError(err));
      });
  };
};

export const logoutUser = () => {
  return (dispatch) => {
    dispatch(loginRequest());
    fetch("http://localhost:5000/logout", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => {
        if (res.status === 200) {
          dispatch(loginError());
        }
      })
      .catch((err) => {
        dispatch(loginError(err));
      });
  };
};

export const fetchUsersEditProfile = (data,verifyToken) => {
  return (dispatch) => {
    fetch(`http://localhost:5000/MyAccount`, {
      method: "POST",
      body: JSON.stringify(data),
      headers: { 
        "Content-Type": "application/json",
        authorization: `${verifyToken}`,
      },
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
        const user = { data: res.data, verifyToken: res.verifyToken };

        dispatch(loginSuccess(user));
        dispatch(editProfile());
      });
  };
};

export const fetchUserDeleteAccount = () => {
  return (dispatch) => {
    fetch(`http://localhost:5000/MyAccount`, {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => {
        dispatch(editProfile());

        dispatch(loginError());
      })
      .catch((err) => {
        dispatch(loginError(err));
      });
  };
};

export const loginRequest = () => {
  return {
    type: LOGIN_REQUEST,
  };
};

export const loginSuccess = (res) => {
  return {
    type: LOGIN_SUCCES,
    payload: res,
  };
};

export const loginError = (err) => {
  return {
    type: LOGIN_ERROR,
    payload: err,
  };
};

export const editProfile = () => {
  return {
    type: EDIT_PROFILE,
  };
};
