import React from "react";
import { connect } from "react-redux";
import { Route, Redirect } from "react-router-dom";
import { verifyAuthentication, verifyRefreshToken } from "../redux/Authentication/action";

const PublicRoute = ({
  isAuth,
  loaded,
  loading,
  client,
  verifyAuthentication,
  verifyRefreshToken,
  component: Component,
  ...rest
}) => {
  if (!loaded) {
    if (!client.verifyToken || client.verifyToken == undefined) {
      verifyRefreshToken();
    } else {

      verifyAuthentication(client.verifyToken);
    }
  }
  if (loading) {
    return <h1>Loading </h1>;
  } else if (!loading) {
    console.log("you are in public");
    console.log(isAuth);
    return (
      <Route
        {...rest}
        component={(props) =>
          !isAuth ? <Component {...props} /> : <Redirect to="/" />
        }
      />
    );
  }
};

const mapStatetoProps = (state) => ({
  isAuth: state.users.isAuth,
  locales: state.locale.lang,
  client: state.users.user,
  loaded: state.users.loaded,
  loading: state.users.loading,
});

const mapDispatchToProps = (dispatch) => {
  return {
    verifyRefreshToken: () => dispatch(verifyRefreshToken()),

    verifyAuthentication: (verifyToken) =>
      dispatch(verifyAuthentication(verifyToken)),
  };
};
export default connect(mapStatetoProps, mapDispatchToProps)(PublicRoute);
