import React from "react";
import { Link } from "react-router-dom";
import Email from "./../images/email.png"
import { connect } from "react-redux";

const EmailConfirmation = () => {
  
  return (
    <div className="email-confirmation">
        <img src={Email} className="email"    />
         <div class="email-text"> 
         <p>A confirmation link has been sent to you</p>
              <p>--- Please check your email address!---</p>
                  <Link to="/" className="link-home" >Back to login page!</Link>
        </div>
               
   
    </div>
  );
};
const mapStateToProps = (state) => {
  return { 
    register:state.users.register,
  };
};
export default  connect(mapStateToProps)(EmailConfirmation);
