import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import Cookies from "js-cookie";
import Homepage from "./Homepage";
import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Link,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";
import {
  fetchUsersData,
  fetchUsersLogin,
  loginSuccess,
  logoutUser,
  verifyAuthentication,
} from "../redux/Authentication/action";

class Home extends Component {
  render() {
    return (
      <div>
        <h1>
          Welcome in app, {this.props.client[0].firstname}
          <fieldset>
           <legend> <Link to="/MyAccount"> My Account </Link></legend>
          </fieldset>
        </h1>
        <button type="submit" onClick={this.props.logoutUser}>
          Logout
        </button>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    client: state.users.user,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
    logoutUser: () => dispatch(logoutUser()),

  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
