import React, { Component } from "react";
import { Link } from "react-router-dom";
import translate from "../i18nProvider/translate";
import { I18nProvider, LOCALES } from "../i18nProvider";
import { setLocales } from "../redux/Language/action";
import { connect } from "react-redux";
import { NavLink, withRouter, Redirect } from "react-router-dom";
import {
  verifyAuthentication,
  logoutUser,
  fetchUserDeleteAccount,
  fetchUsersEditProfile,
  editProfile,
} from "../redux/Authentication/action";
class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: this.props.client.data[0].email,
      password: this.props.client.data[0].firstname,
      password_confirm: this.props.client.data[0].email,
      firstName: this.props.client.data[0].firstname,
      lastName: this.props.client.data[0].lastname,
      phone: this.props.client.data[0].phone,
    };
  }
  postEdit = () => {
    const data = {
      email: this.state.email,
      password: this.state.password,
      password_confirm: this.state.password_confirm,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      phone: this.state.phone,
    };
    console.log(`aici esti in post edit cu data: ${data.email}`);
    console.log(`aici esti in post edit cu verifytoken: ${this.props.client.verifyToken}`);

    this.props.fetchUsersEditProfile(data, this.props.client.verifyToken);
  };
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    console.log("Profile 33333333edit");

    e.preventDefault();
    this.postEdit();
    console.log("1111111111111111");
  };
  handleDelete = () => {
    this.props.fetchUserDeleteAccount();
  };

  render() {
    console.log("Profile edit");
    console.log(`in profile ${this.props.client.verifyToken}`);

    return !this.props.edit ? (
      <div>
        <div>
          <form>
            <fieldset>
              <legend>
                <h2>Profile</h2>
              </legend>
              <label>Id: {this.props.client.data[0].id}</label>
              <br></br>
              <label>First name: {this.props.client.data[0].firstname}</label>
              <br></br>
              <label>Last name: {this.props.client.data[0].lastname}</label>
              <br></br>
              <label>Email: {this.props.client.data[0].email}</label>
              <br></br>
              <label>Phone number: {this.props.client.data[0].phone}</label>
              <br></br>
              <button onClick={this.props.editProfile}>Edit Profile</button>
            </fieldset>
          </form>
        </div>
      </div>
    ) : (
      <div>
          

        <form onSubmit={this.handleSubmit}>
          <fieldset>
            <legend>
              <h2>Edit Profile</h2>
            </legend>
            <div>
              <label htmlFor="email">{translate("e-mail")}</label>
              <input
                value={this.state.email}
                type="email"
                name="email"
                placeholder="Email"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="firstName">
                {translate("first-name", "abc")}
              </label>
              <input
                value={this.state.firstName}
                type="text"
                name="firstName"
                placeholder="First Name"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="lastName">{translate("last-name")}</label>
              <input
                value={this.state.lastName}
                type="text"
                name="lastName"
                placeholder="Last Name"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="phone">{translate("phone-number")}</label>
              <input
                value={this.state.phone}
                type="text"
                name="phone"
                placeholder="Phone"
                onChange={this.handleChange}
              />
            </div>
            <div>
              <button type="submit">Save</button>
              <button onClick={this.props.editProfile}>Cancel</button>
            </div>
          </fieldset>
        </form>
        <button onClick={this.handleDelete}>Delete my Account</button>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    client: state.users.user,
    edit: state.users.edit,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser()),
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUserDeleteAccount: () => dispatch(fetchUserDeleteAccount()),
    fetchUsersEditProfile: (data,verifyToken) => dispatch(fetchUsersEditProfile(data,verifyToken)),
    editProfile: () => dispatch(editProfile()),
    verifyAuthentication: (data, verifyToken) =>
      dispatch(verifyAuthentication(data, verifyToken)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
