import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: true,
    };
  }
  change = () => {
    this.setState({
      isLoggedIn: false,
    });
  };
  handleClick = () => {};
  render() {
    if (this.state.isLoggedIn) {
      return (
        <I18nProvider locale={this.props.locales}>
          <div>
            <div>
              <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                English
              </button>{" "}
              |
              <a
                role="button"
                onClick={() => this.props.setLocale(LOCALES.GERMAN)}
              >
                German1asdasda
              </a>
              {""}|
              <a
                role="button"
                onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}
              >
                Romanian
              </a>
            </div>
            <h1>Welcome to the Homepage</h1>
            {/*  <Login goes={this.change} gone={this.state.isLoggedIn} />
                     <Register goes={this.change} gone={this.state.isLoggedIn} />*/}
            <div>
              <NavLink to="/login" activeStyle={{ color: "red" }}>
                Login
              </NavLink>
              {"   "}
              <NavLink to="/register" activeStyle={{ color: "red" }}>
                Register
              </NavLink>
            </div>
          </div>
        </I18nProvider>
      );
    } else if (!this.state.isLoggedIn) {
      return (
        <div>
          <h1>
            <div onClick={this.handleClick()}> MyAccount </div>
          </h1>
        </div>
      );
    }
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
  };
};
// const mapStateToProps = (state) => {
//   return {
//     // locale: state.locale,
//   };
// };
// const mapDispatchToProps = (dispatch) => {
//   return {
//     //setLocale: (lang) => dispatch(setEnglish(lang)),
//   };
// };
const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
