import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import Cookies from "js-cookie";

import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Link,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";
import {
  fetchUsersData,
  fetchUsersLogin,
  logoutUser,
  verifyAuthentication,
} from "../redux/Authentication/action";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userss: [],
    };
  }
  // change = () => {
  //   this.setState({
  //     isLoggedIn: false,
  //   });
  // };
  // handleClick = () => {
  //   Cookies.remove('jwt')
  //   this.props.verifyAuthentication()
  // };
  handleChange = () => {
    this.setState({ userss: this.props.client });
  };

  render() {
    //

    console.log("I am in home first");
    //
    if (!this.props.loaded) {
      //
      this.props.verifyAuthentication();
      console.log("you make an action => loaded = true ");
      //
    }
    //
    if (this.props.loading) {
      //
      return (
        <h1>
          Loading... {console.log("You are in loading => loading = true")}
        </h1>
      );
      //
    } else if (!this.props.loading) {
      //
      console.log("You are in loading => loading = false");
      //
      if (!this.props.isAuth) {
        return (
          <I18nProvider locale={this.props.locales}>
            <div>
              <div>
                <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                  English
                </button>{" "}
                |{" "}
                <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
                  German
                </button>{" "}
                {""}|{" "}
                <button onClick={() => this.props.setLocale(LOCALES.ROMANIAN)}>
                  Romanian
                </button>{" "}
              </div>
              <h1>
                Welcome to the Homepage and isAuth is:
                {/* {console.log(this.props.users)}{" "}
                {console.log(this.props.users)}
                {console.log(this.props.users.email)} */}
              </h1>
              {/* {    <button onClick="">GETGETGET</button> */}
              {/*  <Login goes={this.change} gone={this.state.isLoggedIn} />
                     <Register goes={this.change} gone={this.state.isLoggedIn} />*/}
              <div>
                <NavLink to="/login" activeStyle={{ color: "red" }}>
                  Login
                </NavLink>{" "}
                <NavLink to="/register" activeStyle={{ color: "red" }}>
                  Register
                </NavLink>
              </div>
            </div>
          </I18nProvider>
        );
      } else if (this.props.isAuth) {
        return (
          <div>
            {console.log(this.props.client)}

            <h1>
              Welcome in app{" "}{this.state.userss}
              {/* {this.state.userss.map((user) => (
                <li>{user.email}</li>
              ))}{" "} */}
              <Link to="/MyAccount"> MyAccount </Link>
            </h1>
            <button type="submit" onClick={this.props.logoutUser}>
              Logout
            </button>
          </div>
        );
      }
    }
  } ////////////////
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    client: state.users.users,
    loaded: state.users.loaded,
    isAuth: state.users.isAuth,
    loading: state.users.loading,
  };
};
// const mapStateToProps = (state) => {
//   return {
//     // locale: state.locale,
//   };
// };
// const mapDispatchToProps = (dispatch) => {
//   return {
//     //setLocale: (lang) => dispatch(setEnglish(lang)),
//   };
// };
const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUsersLogin: (data) => dispatch(fetchUsersLogin(data)),
    fetchUsersData: () => dispatch(fetchUsersData()),
    verifyAuthentication: () => dispatch(verifyAuthentication()),
    logoutUser: () => dispatch(logoutUser()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
