import React, { Component } from "react";
import Register from "./Register";
import { Link } from "react-router-dom";
import translate from "../i18nProvider/translate";
import { I18nProvider, LOCALES } from "../i18nProvider";
import { setLocales } from "../redux/Language/action";
import { connect } from "react-redux";
import { NavLink, withRouter, Redirect } from "react-router-dom";
import Cookie from "js-cookie";
import {
  verifyAuthentication,
  logoutUser,
  editProfile,
} from "../redux/Authentication/action";
class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "asdas",
      password: "",
      password_confirm: "",
      firstName: "",
      lastName: "",
      phone: "",
    };
  }
  
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    console.log("Profile edit");

    return !this.props.edit ? (
      <div>
        <div>
          <form>
            <fieldset>
              <legend>Profile</legend>
              <label>Id: {this.props.client[0].id}</label>
              <br></br>
              <label>First name: {this.props.client[0].firstname}</label>
              <br></br>
              <label>Last name: {this.props.client[0].lastname}</label>
              <br></br>
              <label>Email: {this.props.client[0].email}</label>
              <br></br>
              <label>Phone number: {this.props.client[0].phone}</label>
              <br></br>
              <button type="submit" onClick={this.props.editProfile}>
                Edit Profile
              </button>
            </fieldset>
          </form>
        </div>
      </div>
    ) : (
      <form onSubmit={this.handleSubmit}>
        <fieldset>
          {/* <h3>{translate("register-")}</h3> */}
          <h1>Edit Profile</h1>
          <div>
            <label htmlFor="email">{translate("e-mail")}</label>
            <input
              type="email"
              name="email"
              placeholder="Email"
              required
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="password_confirm">{translate("pass-word")}</label>
            <input
              type="password"
              placeholder="Confirm Password"
              name="password_confirm"
              required
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="password">{translate("pass-word")}</label>
            <input
              type="password"
              placeholder="Password"
              name="password"
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="firstName">{translate("first-name", "abc")}</label>
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              required
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="lastName">{translate("last-name")}</label>
            <input
              value={this.props.client[0].lastname}
              type="text"
              name="lastName"
              placeholder="Last Name"
              required
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="phone">{translate("phone-number")}</label>
            <input
              type="text"
              name="phone"
              placeholder="Phone"
              onChange={this.handleChange}
            />
          </div>
          {/* <button type="submit">{translate("register-")}</button> */}
          <button type="submit" onClick={this.props.editProfile}>
            Cancel
          </button>{" "}
          <button type="submit" onClick={this.props.editProfile}>
            Save
          </button>
        </fieldset>
      </form>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isAuth: state.users.isAuth,
    client: state.users.user,
    edit: state.users.edit,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser()),
    setLocale: (lang) => dispatch(setLocales(lang)),
    editProfile: () => dispatch(editProfile()),

    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};
// export default MyAccount;

// class MyAccount extends Component {
//   state = {};
//   render() {
//     return <div onClick={this.handleaClink}></div>;
//   }
// }

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
