      headers: { "Content-Type": "application/json" },
      credentials: "include",
    })
      .then((res) => res.json())
      .then((res) => {
  
        const user = res.data;

        dispatch(loginSuccess(user));

      })

      .catch((err) => {
        dispatch(loginError(err));
        console.error(err.value);
      });
  };
};


export const fetchUsersData = (data) => {
