		if (this.props.loading == true) {
			return <div>Loading...</div>;
		} else {
			return (
				<div id='wrapper-image-box' style={{ width: '100%', height: '100%' }}>
					<div
						id='wrapper-image-list'
						style={{
							display: 'flex',
							flexDirection: 'row',
							flexWrap: 'wrap',
							justifyContent: 'center',
						}}>
                        <div>
                            <input type="file" onChange={this.showImage}></input>
                        </div>
                        <div id={'image-box'}>

                        </div>
					</div>
				</div>
			);
		}
	}
}

const mapStateToProps = (state) => {
	return {
		locales: state.locale.lang,
		client: state.users.user,
		image: state.image.image,
		loading: state.image.loading,
	};
};
const mapDispatchToProps = (dispatch) => {
	return {
		findImage: (id) => dispatch(findImages(id)),
	};
};
export default connect(mapStateToProps, mapDispatchToProps)(ImagePageUpload);
