  // };

  handleState = () => {
    console.log(this.props.client);
    console.log(this.state.userss);
    console.log("Inainte de setState");

    this.setState({ userss: this.props.client });
    console.log("Dupa setState");
    console.log(this.state.userss);
    console.log(this.state.userss[0]);
