/** @format */

import React, { Component } from 'react';
import { connect } from 'react-redux';
import { findImages } from '../redux/image/action';

export class ImagePageUpload extends Component {
	constructor(props) {
		super(props);
		this.state = {
			imageList: [],
			selectedFile: [],
		};
	}

	componentDidMount = () => {
		console.log(this.props.client);
		this.props.findImage(this.props.client.data.email);
	};

	showImage = (e) => {
		if (e.target.files.length == 0) {
			return;
		}
		var images = new Array();
		var reader = new Array();
		for (let i = 0; i < e.target.files.length; i++) {
			reader.push(new FileReader());
			images.push(e.target.files[i]);
		}
		for (let j = 0; j < e.target.files.length; j++) {
			reader[j].readAsDataURL(e.target.files[j]);
		}
		reader.map((item) => {
			item.onload = () => {
				if (item.readyState == 2) {
                    this.state.selectedFile.push({name:images[0].name,})
					console.log(item);
					console.log('IMTEM ITEM');
					var divId = document.createElement('div');

					var image = document.createElement('img');
					image.src = item.result;
					image.width = 146;
					image.height = 136;
					divId.appendChild(image);
					document.getElementById('image-box').appendChild(divId);
				}
			};
		});
	};
    uploadImage=()=>{


    }
	render() {
		console.log(this.props.image);
		console.log('imagee');
		if (this.props.loading == true) {
			return <div>Loading...</div>;
		} else {
			return (
				<div id='wrapper-image-box'>
					<div
						id='wrapper-image-list'
						style={{
							display: 'flex',
							flexFlow: 'row wrap',
							justifyContent: 'space-between',
							flexDirection: 'column',
							flexWrap: 'wrap',
							alignContent: 'center',
							alignItems: 'center',
						}}>
						<div>
							<input type='file' onChange={this.showImage}></input>
						</div>
						<div id={'image-box'}></div>
                        <button onClick={this.uploadImage}>Incarca imaginea</button>
					</div>
				</div>
			);
		}
	}
}

const mapStateToProps = (state) => {
	return {
		locales: state.locale.lang,
		client: state.users.user,
		image: state.image.image,
		loading: state.image.loading,
	};
};
const mapDispatchToProps = (dispatch) => {
	return {
		findImage: (id) => dispatch(findImages(id)),
	};
};
export default connect(mapStateToProps, mapDispatchToProps)(ImagePageUpload);
