import React, { Component } from "react";
import Register from "./Register";
import { Link } from "react-router-dom";
import translate from "../i18nProvider/translate";
import { I18nProvider, LOCALES } from "../i18nProvider";
import { setLocales } from "../redux/Language/action";
import { connect } from "react-redux";
import { NavLink, withRouter, Redirect } from "react-router-dom";
import Cookie from "js-cookie";
import {
  verifyAuthentication,
  logoutUser,
  fetchUsersEditProfile,
  editProfile,
} from "../redux/Authentication/action";
class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: this.props.client[0].email,
      password: this.props.client[0].firstname,
      password_confirm: this.props.client[0].email,
      firstName: this.props.client[0].firstname,
      lastName: this.props.client[0].lastname,
      phone: this.props.client[0].phone,
    };
  }
  postEdit = () => {
    const data = {
      email: this.state.email,
      password: this.state.password,
      password_confirm: this.state.password_confirm,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      phone: this.state.phone,
    };
    this.props.fetchUsersEditProfile(data);
    //this.props.editProfile()
    console.log("22222222222222222222222");
    // this.props.fetchUsersRegister_GET(data)
    // if (this.props.registerSucces) this.props.goes();
  };
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    console.log("Profile 33333333edit");

    e.preventDefault();
    this.postEdit();
    console.log("1111111111111111");
  };

  render() {
    console.log("Profile edit");

    return !this.props.edit ? (
      <div>
        <div>
          <form>
            <fieldset>
              <legend>
                <h2>Profile</h2>
              </legend>
              <label>Id: {this.props.client[0].id}</label>
              <br></br>
              <label>First name: {this.props.client[0].firstname}</label>
              <br></br>
              <label>Last name: {this.props.client[0].lastname}</label>
              <br></br>
              <label>Email: {this.props.client[0].email}</label>
              <br></br>
              <label>Phone number: {this.props.client[0].phone}</label>
              <br></br>
              <button onClick={this.props.editProfile}>Edit Profile</button>
            </fieldset>
          </form>
        </div>
      </div>
    ) : (
      <div>
        <form onSubmit={this.handleSubmit}>
          <fieldset>
            {/* <h3>{translate("register-")}</h3> */}
            <legend>
              <h2>Edit Profile</h2>
            </legend>
            <div>
              <label htmlFor="email">{translate("e-mail")}</label>
              <input
                value={this.state.email}
                type="email"
                name="email"
                placeholder="Email"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="firstName">
                {translate("first-name", "abc")}
              </label>
              <input
                value={this.state.firstName}
                type="text"
                name="firstName"
                placeholder="First Name"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="lastName">{translate("last-name")}</label>
              <input
                value={this.state.lastName}
                type="text"
                name="lastName"
                placeholder="Last Name"
                required
                onChange={this.handleChange}
              />
            </div>
            <div>
              <label htmlFor="phone">{translate("phone-number")}</label>
              <input
                value={this.state.phone}
                type="text"
                name="phone"
                placeholder="Phone"
                onChange={this.handleChange}
              />
            </div>
            <input type="button" onClick={this.handleDelete}>
          Delete Account
        </input>
            {/* <button type="submit">{translate("register-")}</button> */}
            <button type="submit">Save</button>
            <button onClick={this.props.editProfile}>Cancel</button>
          </fieldset>
        </form>
     
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isAuth: state.users.isAuth,
    client: state.users.user,
    edit: state.users.edit,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    logoutUser: () => dispatch(logoutUser()),
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUsersEditProfile: (data) => dispatch(fetchUsersEditProfile(data)),
    editProfile: () => dispatch(editProfile()),
    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};
// export default MyAccount;

// class MyAccount extends Component {
//   state = {};
//   render() {
//     return <div onClick={this.handleaClink}></div>;
//   }
// }

export default connect(mapStateToProps, mapDispatchToProps)(MyAccount);
