import React, { Component } from "react";
import Login from "./Login";
import Register from "./Register";
import { FormattedMessage } from "react-intl";
import MyAccount from "./MyAccount";
import { connect } from "react-redux";
import { setLocales } from "../redux/Language/action";
import { I18nProvider, LOCALES } from "../i18nProvider";
import {
  BrowserRouter,
  Route,
  Router,
  Link,
  Switch,
  NavLink,
} from "react-router-dom";
import translate from "../i18nProvider/translate";
import { fetchUsersData } from "../redux/Authentication/action";

class Home extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     isLoggedIn: true,
  //   };
  // }
  // change = () => {
  //   this.setState({
  //     isLoggedIn: false,
  //   });
  // };
  // handleClick = () => {};
  render() {
    if (!this.props.isAuth) {
      return (
        <I18nProvider locale={this.props.locales}>
          <div>
            <div>
              <button onClick={() => this.props.setLocale(LOCALES.ENGLISH)}>
                English
              </button>{" "}
              |{" "}
              <button onClick={() => this.props.setLocale(LOCALES.GERMAN)}>
                German
              </button>{" "}
              {""}|{" "}
              <button onClick={() => this.props.setLocale(LOCALES.ROMANIA)}>
                Romanian
              </button>{" "}
            </div>
            <h1>Welcome to the Homepage</h1>
            {/* {    <button onClick="">GETGETGET</button> */}
            {/*  <Login goes={this.change} gone={this.state.isLoggedIn} />
                     <Register goes={this.change} gone={this.state.isLoggedIn} />*/}
            <div>
              <NavLink to="/login" activeStyle={{ color: "red" }}>
                Login
              </NavLink>{" "}
              <NavLink to="/register" activeStyle={{ color: "red" }}>
                Register
              </NavLink>
            </div>
          </div>
        </I18nProvider>
      );
    } else {
      return (
        <div>
          <h1>
            Welcome in app
            <Link to="./MyAccount">MyAccount</Link>
          </h1>
        </div>
      );
    }
  }
}
const mapStateToProps = (state) => {
  return {
    locales: state.locale.lang,
    isNot: state.users.isAuth,
  };
};
// const mapStateToProps = (state) => {
//   return {
//     // locale: state.locale,
//   };
// };
// const mapDispatchToProps = (dispatch) => {
//   return {
//     //setLocale: (lang) => dispatch(setEnglish(lang)),
//   };
// };
const mapDispatchToProps = (dispatch) => {
  return {
    setLocale: (lang) => dispatch(setLocales(lang)),
    fetchUsersData: (data) => dispatch(fetchUsersData(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
