import React from "react";
import { connect } from "react-redux";
import { Route, Redirect } from "react-router-dom";
import Home from "./Home";
import Cookie from "js-cookie";
import { verifyAuthentication } from "../redux/Authentication/action";

const PublicRoute = ({
  isAuth,
  verifyAuthentication,
  component: Component,
  ...rest
}) => {
  console.log("you are in public");

  return (
    <Route
      {...rest}
      component={(props) =>
        !isAuth ? <Component {...props} /> : <Redirect to="/MyAccount" />
      }
    />
  );
};

const mapStatetoProps = (state) => ({
  isAuth: state.users.isAuth,
  locales: state.locale.lang,
});

const mapDispatchToProps = (dispatch) => {
  return {
    verifyAuthentication: () => dispatch(verifyAuthentication()),
  };
};
export default connect(mapStatetoProps, mapDispatchToProps)(PublicRoute);
