import { LOGIN_REQUEST, LOGIN_SUCCES, LOGIN_ERROR } from "./actionType";
import Cookie from "js-cookie";

const initialState = {
  user:[{id:'121', email:'asa@12'}],
  loading: true,
  loaded: false,
  error: null,
  isAuth: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state, //            1   loading:true     loaded:false
        loading: true, //          2   loading:true      loaded:true
        loaded: true, //        3   loading:false     loaded:true
      };
    case LOGIN_SUCCES:
      return {
        ...state,
        loading: false,
        loaded: true,
        isAuth: true,
        users: [{id:'0000', email:''}],
      };
    case LOGIN_ERROR:
      return {
        ...state,
        loading: false,
        loaded: true,
        error: action.payload,
        isAuth: false,
      };
    default:
      return state;
  }
};

export default userReducer;
